PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE BLOCKS(BLOCKID INTEGER PRIMARY KEY AUTOINCREMENT, TITLE CHAR(50) NOT NULL, CREATED DATETIME NOT NULL, TYPE INTEGER NOT NULL);
CREATE TABLE ROLES(ROLEID INTEGER PRIMARY KEY AUTOINCREMENT, NAME VARCHAR(10) NOT NULL);
CREATE TABLE CATEGORIES(CATEGORYID INTEGER PRIMARY KEY AUTOINCREMENT, CATEGORYTEXT VARCHAR(500) NOT NULL, PARENT INTEGER NOT NULL, CREATED DATETIME NOT NULL);
CREATE TABLE BLOCKS_CATEGORIES (BCID INTEGER PRIMARY KEY AUTOINCREMENT, CATEGORYID INTEGER NOT NULL, BLOCKID INTEGER NOT NULL, FOREIGN KEY (BLOCKID) REFERENCES BLOCKS(BLOCKID) FOREIGN KEY (CATEGORYID) REFERENCES CATEGORIES(CATEGORYID));
CREATE TABLE REVISIONS(REVISIONID INTEGER PRIMARY KEY AUTOINCREMENT, TEXT VARCHAR(1000), BLOCKID INTEGER NOT NULL, CREATED DATETIME NOT NULL, USERID INTEGER NOT NULL, status integer not null default 0);
CREATE TABLE RATINGS (RATINGID INTEGER PRIMARY KEY AUTOINCREMENT, ENTITYID INTEGER NOT NULL, RATING INTEGER NOT NULL, TYPE INTEGER);
CREATE TABLE users ( userid integer primary key autoincrement, username varchar(20) not null, password char(60) not null);
DELETE FROM sqlite_sequence;
COMMIT;
